import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import sampleChallenges from '../data/sampleChallenges';

const RPGDashboard = () => {
  const { id } = useParams();
  const [session, setSession] = useState(null);
  const [quest, setQuest] = useState(null);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
    });
  }, []);

  useEffect(() => {
    const questData = sampleChallenges.find(q => q.id === parseInt(id));
    setQuest(questData);
  }, [id]);

  if (!session) return <p className="text-white p-6">Loading session...</p>;
  if (!quest) return <p className="text-white p-6">Quest not found</p>;

  return (
    <div className="px-6 py-8 text-white min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900">
      <h1 className="text-3xl font-bold mb-2">{quest.title}</h1>
      <p className="text-gray-400 mb-4">{quest.description}</p>
      <div className="bg-gray-800 p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Tutorial</h2>
        <pre className="whitespace-pre-wrap text-sm text-gray-300">{quest.tutorial}</pre>
      </div>
    </div>
  );
};

export default RPGDashboard;
