// src/pages/RPGDashboard.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import sampleChallenges from '../data/sampleChallenges';

const RPGDashboard = () => {
  const { id } = useParams();
  const [session, setSession] = useState(null);
  const [quest, setQuest] = useState(null);

  useEffect(() => {
    // Get session from Supabase
    supabase.auth.getSession().then(({ data: { session } }) => {
      console.log("🔁 Session loaded in RPG:", session);
      setSession(session);
    });
  }, []);

  useEffect(() => {
    const questData = sampleChallenges.find(q => q.id === parseInt(id));
    setQuest(questData);
  }, [id]);

  if (!session) return <p className="text-white p-6">Loading session in RPG...</p>;
  if (!quest) return <p className="text-white p-6">Loading quest...</p>;

  return (
    <div className="px-6 py-8 text-white min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-1">{quest.title}</h1>
        <p className="text-gray-400">{quest.description}</p>
      </div>

      <div className="bg-gray-800 p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Tutorial</h2>
        <pre className="text-sm text-gray-300 whitespace-pre-wrap">
          {quest.tutorial}
        </pre>
      </div>

      <div className="mt-6 bg-gray-800 p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">Quiz (coming soon)</h2>
        <p className="text-gray-400">You'll be quizzed on this quest after completing the tutorial!</p>
      </div>
    </div>
  );
};

export default RPGDashboard;
