import React, { useEffect, useState } from "react";
import { supabase } from "./supabaseClient";
import Leaderboard from "./Leaderboard";

export default function App() {
  const [user, setUser] = useState(null);

  // Check if the user is already signed in on page load
  useEffect(() => {
    supabase.auth.getUser().then(({ data: { user } }) => {
      setUser(user);
    });

    // Listen for auth state changes
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  useEffect(() => {
    if (user) {
      upsertPlayer(user);
    }
  }, [user]);

  const login = async () => {
    await supabase.auth.signInWithOAuth({
      provider: "github",
      options: {
        redirectTo: "http://localhost:3000", // LOCAL testing only
      },
    });
  };

  const logout = async () => {
    await supabase.auth.signOut();
  };

  const upsertPlayer = async (user) => {
    const { error } = await supabase
      .from("players")
      .upsert([
        {
          user_id: user.id,
          email: user.email,
          level: 1,
          xp: 0,
          updated_at: new Date(),
        },
      ])
      .eq("user_id", user.id);

    if (error) {
      console.error("Error upserting player:", error.message);
    }
  };

  return (
    <div className="p-8 text-center font-sans bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-bold text-blue-600 mb-4">⚔️ Cyber Quest</h1>
      {user ? (
        <>
          <p className="text-lg mb-4">Welcome, {user.email}!</p>
          <button
            onClick={logout}
            className="mb-6 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
          >
            Logout
          </button>
          <Leaderboard />
        </>
      ) : (
        <button
          onClick={login}
          className="mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition"
        >
          Login with GitHub
        </button>
      )}
    </div>
  );
}
