// src/components/Leaderboard.js
import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

function Leaderboard() {
  const [players, setPlayers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [userRank, setUserRank] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      // Get current session
      const { data: sessionData } = await supabase.auth.getSession();
      const user = sessionData?.session?.user;

      if (!user) return;
      setCurrentUser(user);

      // Fetch all players
      const { data, error } = await supabase
        .from('Players')
        .select('id, username, level, xp')
        .order('xp', { ascending: false });

      if (!error && data) {
        setPlayers(data);

        // Find current player & rank
        const index = data.findIndex((p) => p.id === user.id);
        if (index !== -1) {
          setCurrentPlayer(data[index]);
          setUserRank(index + 1);
        }
      } else {
        console.error('Leaderboard error:', error);
      }
    };

    fetchLeaderboard();
  }, []);

  const getXPPercent = (xp) => {
    const xpPerLevel = 100; // or make it dynamic later
    return Math.min((xp % xpPerLevel) / xpPerLevel, 1) * 100;
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white px-4 py-8 flex flex-col items-center">
      <div className="bg-gray-800 p-6 rounded shadow-lg w-full max-w-2xl">
        <h2 className="text-2xl font-bold mb-6 text-center">üèÜ CyberQuest Leaderboard</h2>

        {currentPlayer && (
          <div className="mb-6 bg-gray-700 p-4 rounded shadow text-center">
            <p className="text-purple-300 font-semibold text-lg">
              You: {currentPlayer.username} ‚Äî Rank #{userRank}
            </p>
            <p className="text-sm text-gray-400">Level {currentPlayer.level} | XP: {currentPlayer.xp}</p>
            <div className="h-2 bg-gray-600 rounded mt-2">
              <div
                className="bg-green-500 h-2 rounded"
                style={{ width: `${getXPPercent(currentPlayer.xp)}%` }}
              />
            </div>
          </div>
        )}

        <ul className="space-y-4">
          {players.map((player, index) => (
            <li key={player.id} className="bg-gray-700 p-4 rounded shadow">
              <div className="flex justify-between items-center">
                <div>
                  <span className="font-bold text-purple-200">
                    #{index + 1} {player.username}
                  </span>
                  <p className="text-sm text-gray-300">Level {player.level} ‚Ä¢ XP: {player.xp}</p>
                </div>
                <div className="w-32 h-2 bg-gray-600 rounded ml-4">
                  <div
                    className="bg-blue-500 h-2 rounded"
                    style={{ width: `${getXPPercent(player.xp)}%` }}
                  />
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Leaderboard;
