k// src/components/Layout.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { Menu } from 'lucide-react';

function Layout({ children }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [session, setSession] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
    });

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
    });

    return () => {
      listener?.subscription.unsubscribe();
    };
  }, []);

  useEffect(() => {
    const fetchUserData = async () => {
      if (session) {
        const { data } = await supabase
          .from('Players')
          .select('username, email, phone_number, xp, level')
          .eq('id', session.user.id)
          .single();
        setUserData(data);
      }
    };
    fetchUserData();
  }, [session]);

  const handleLogout = async () => {
    await supabase.auth.signOut();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <header className="flex justify-between items-center p-4 bg-gray-800 shadow-md">
        <h1 className="text-2xl font-bold">CyberQuest</h1>
        <button onClick={() => setMenuOpen(!menuOpen)}>
          <Menu size={28} />
        </button>
      </header>

      {menuOpen && (
        <div className="bg-gray-800 shadow p-4">
          <nav className="flex flex-col gap-2">
            <Link to="/" onClick={() => setMenuOpen(false)} className="hover:underline">Home</Link>
            <Link to="/leaderboard" onClick={() => setMenuOpen(false)} className="hover:underline">Leaderboard</Link>
            <Link to="/phishing" onClick={() => setMenuOpen(false)} className="hover:underline">Phishing Quest</Link>
            {session && <button onClick={handleLogout} className="text-left hover:underline">Logout</button>}
          </nav>

          {session && userData && (
            <div className="mt-4 p-4 border border-gray-600 rounded">
              <h2 className="text-lg font-semibold">Profile</h2>
              <p><strong>Username:</strong> {userData.username}</p>
              <p><strong>Email:</strong> {userData.email}</p>
              <p><strong>Phone:</strong> {userData.phone_number || 'N/A'}</p>
              <p><strong>XP:</strong> {userData.xp}</p>
              <p><strong>Level:</strong> {userData.level}</p>
            </div>
          )}
        </div>
      )}

      <main>{children}</main>
    </div>
  );
}

export default Layout;
