import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { Menu, X } from 'lucide-react';

function Layout({ children }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = async () => {
    await supabase.auth.signOut();
    navigate('/login');
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-900 text-white">
      {/* Navbar */}
      <nav className="bg-gray-800 px-4 py-3 shadow flex justify-between items-center">
        <Link to="/" className="text-xl font-bold text-purple-400">CyberQuest</Link>
        <div className="md:hidden">
          <button onClick={() => setMenuOpen(!menuOpen)} className="text-white focus:outline-none">
            {menuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
        <div className="hidden md:flex gap-6 items-center">
          <Link to="/" className="hover:text-purple-400">Home</Link>
          <Link to="/leaderboard" className="hover:text-purple-400">Leaderboard</Link>
          <Link to="/profile" className="hover:text-purple-400">Profile</Link>
          <button onClick={handleLogout} className="text-sm px-3 py-1 bg-purple-600 hover:bg-purple-700 rounded">
            Logout
          </button>
        </div>
      </nav>

      {/* Mobile Dropdown */}
      {menuOpen && (
        <div className="md:hidden bg-gray-800 border-t border-gray-700 px-4 py-2 space-y-2">
          <Link to="/" className="block hover:text-purple-400" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/leaderboard" className="block hover:text-purple-400" onClick={() => setMenuOpen(false)}>Leaderboard</Link>
          <Link to="/profile" className="block hover:text-purple-400" onClick={() => setMenuOpen(false)}>Profile</Link>
          <button
            onClick={() => {
              setMenuOpen(false);
              handleLogout();
            }}
            className="block w-full text-left text-sm bg-purple-600 hover:bg-purple-700 rounded px-3 py-1"
          >
            Logout
          </button>
        </div>
      )}

      {/* Main content */}
      <main className="flex-1">{children}</main>
    </div>
  );
}

export default Layout;
