// src/components/Layout.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { Menu, User, LogOut, Home, Trophy } from 'lucide-react';

function Layout({ children }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const {
        data: { session },
      } = await supabase.auth.getSession();

      if (session?.user) {
        const { data, error } = await supabase
          .from('Players')
          .select('username, email, phone, xp, level, avatar_url')
          .eq('id', session.user.id)
          .single();

        if (data) setUserData(data);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = async () => {
    await supabase.auth.signOut();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white">
      <nav className="flex items-center justify-between px-4 py-3 bg-gray-900 shadow">
        <Link to="/" className="text-xl font-bold text-purple-400">
          CyberQuest
        </Link>
        <button
          className="md:hidden text-white"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          <Menu size={24} />
        </button>
        <div className={`md:flex items-center space-x-6 ${menuOpen ? 'block' : 'hidden'} md:block`}>
          <Link to="/" className="hover:text-purple-400 flex items-center gap-1">
            <Home size={18} /> Home
          </Link>
          <Link to="/leaderboard" className="hover:text-purple-400 flex items-center gap-1">
            <Trophy size={18} /> Leaderboard
          </Link>
          <div className="hover:text-purple-400 flex items-center gap-1 cursor-pointer" onClick={() => setMenuOpen(!menuOpen)}>
            <User size={18} /> Profile
          </div>
          <button onClick={handleLogout} className="hover:text-red-500 flex items-center gap-1">
            <LogOut size={18} /> Logout
          </button>
        </div>
      </nav>

      {menuOpen && userData && (
        <div className="bg-gray-800 p-4 text-sm border-t border-gray-700 md:hidden">
          <div className="mb-2">
            <strong>Username:</strong> {userData.username || 'N/A'}
          </div>
          <div className="mb-2">
            <strong>Email:</strong> {userData.email || 'N/A'}
          </div>
          <div className="mb-2">
            <strong>Phone:</strong> {userData.phone || 'N/A'}
          </div>
          <div className="mb-2">
            <strong>Level:</strong> {userData.level || 1}
          </div>
          <div className="mb-2">
            <strong>XP:</strong> {userData.xp || 0}
          </div>
          {userData.avatar_url && (
            <img
              src={userData.avatar_url}
              alt="Profile"
              className="mt-4 w-16 h-16 rounded-full object-cover border border-purple-500"
            />
          )}
        </div>
      )}

      <main className="p-4 max-w-7xl mx-auto">{children}</main>
    </div>
  );
}

export default Layout;
