// src/components/Signup.js
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, Link } from 'react-router-dom';
import AuthLayout from './AuthLayout';

function Signup() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '', username: '' });
  const [errorMsg, setErrorMsg] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();
    const { data, error: signUpError } = await supabase.auth.signUp({
      email: form.email,
      password: form.password,
    });

    if (signUpError) return setErrorMsg(signUpError.message);

    // Insert into Players table
    if (data.user) {
      await supabase.from('Players').insert([
        {
          id: data.user.id,
          username: form.username,
          email: form.email,
          level: 1,
          xp: 0,
        },
      ]);
    }

    navigate('/');
  };

  return (
    <AuthLayout>
      <h2 className="text-3xl font-bold mb-6 text-center text-purple-400">üßô‚Äç‚ôÇÔ∏è Create Your Account</h2>
      {errorMsg && <p className="text-red-400 mb-2 text-center">{errorMsg}</p>}
      <form onSubmit={handleSignup} className="space-y-4">
        <input
          type="text"
          placeholder="Username"
          className="w-full p-3 bg-gray-800 rounded"
          onChange={(e) => setForm({ ...form, username: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          className="w-full p-3 bg-gray-800 rounded"
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full p-3 bg-gray-800 rounded"
          onChange={(e) => setForm({ ...form, password: e.target.value })}
          required
        />
        <button className="w-full bg-purple-600 hover:bg-purple-700 px-4 py-3 rounded font-semibold">
          Sign Up
        </button>
      </form>
      <p className="mt-4 text-sm text-gray-400 text-center">
        Already have an account? <Link to="/login" className="text-purple-300 underline">Log in</Link>
      </p>
    </AuthLayout>
  );
}

export default Signup;
