// src/components/Leaderboard.js
import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

function Leaderboard() {
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlayer = async () => {
      const {
        data: { session },
      } = await supabase.auth.getSession();

      if (session?.user?.id) {
        const { data, error } = await supabase
          .from('Players')
          .select('username, xp')
          .eq('id', session.user.id)
          .single();

        if (error) {
          console.error('Error fetching player:', error.message);
        } else {
          setPlayer(data);
        }
      }

      setLoading(false);
    };

    fetchPlayer();
  }, []);

  if (loading) return <p className="text-center text-white mt-8">Loading your profile...</p>;

  return (
    <div className="min-h-screen bg-gray-900 text-white px-4 py-8 flex flex-col items-center">
      <div className="bg-gray-800 p-6 rounded shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Your Leaderboard</h2>
        {player ? (
          <div className="bg-gray-700 rounded p-4 text-center">
            <p className="text-lg font-semibold">Username: {player.username}</p>
            <p className="text-sm mt-2 text-green-400">XP: {player.xp ?? 0}</p>
          </div>
        ) : (
          <p className="text-center text-gray-400">No profile found.</p>
        )}
      </div>
    </div>
  );
}

export default Leaderboard;
