// src/components/Signup.js
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, Link } from 'react-router-dom';

function Signup() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    phone: '',
    password: '',
    username: '',
  });
  const [errorMsg, setErrorMsg] = useState('');

  const validatePhone = (phone) => {
    const pattern = /^\+?[1-9]\d{9,14}$/; // E.164 format
    return pattern.test(phone);
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setErrorMsg('');

    if (!validatePhone(form.phone)) {
      setErrorMsg('Enter a valid phone number (e.g., +919876543210)');
      return;
    }

    if (!form.username.trim()) {
      setErrorMsg('Username is required');
      return;
    }

    const { data, error } = await supabase.auth.signUp({
      phone: form.phone,
      password: form.password,
    });

    if (error) return setErrorMsg(error.message);

    const userId = data?.user?.id;
    if (!userId) return setErrorMsg('Signup failed. No user ID returned.');

    const { error: insertError } = await supabase.from('Players').insert([
      {
        id: userId,
        username: form.username,
        phone: form.phone,
        xp: 0,
        level: 1,
      },
    ]);

    if (insertError) return setErrorMsg('Error saving profile: ' + insertError.message);

    navigate('/');
  };

  return (
    <div className="max-w-md mx-auto mt-10 text-white">
      <h2 className="text-2xl font-bold mb-4">Create Your CyberQuest Account</h2>
      {errorMsg && <p className="text-red-400 mb-2">{errorMsg}</p>}
      <form onSubmit={handleSignup} className="space-y-4">
        <input
          type="text"
          placeholder="Username"
          className="w-full p-2 bg-gray-800 rounded"
          value={form.username}
          onChange={(e) => setForm({ ...form, username: e.target.value })}
        />
        <input
          type="tel"
          placeholder="+91XXXXXXXXXX"
          className="w-full p-2 bg-gray-800 rounded"
          value={form.phone}
          onChange={(e) => setForm({ ...form, phone: e.target.value })}
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full p-2 bg-gray-800 rounded"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
        />
        <button className="w-full bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded font-semibold">
          Sign Up
        </button>
      </form>
      <p className="mt-4 text-sm text-gray-400">
        Already have an account? <Link to="/login" className="text-purple-400 underline">Login</Link>
      </p>
    </div>
  );
}

export default Signup;
