// src/components/Profile.js
import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

function Profile() {
  const [profile, setProfile] = useState(null);
  const [session, setSession] = useState(null);
  const [email, setEmail] = useState('');
  const [uploading, setUploading] = useState(false);
  const [previewUrl, setPreviewUrl] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const { data: sessionData } = await supabase.auth.getSession();
      const user = sessionData?.session?.user;
      if (!user) return;

      setSession(user);
      setEmail(user.email);

      const { data, error } = await supabase
        .from('Players')
        .select('username, level, xp, phone, avatar_url')
        .eq('id', user.id)
        .single();

      if (data) {
        setProfile(data);
        setPreviewUrl(data.avatar_url);
      } else {
        console.error('Failed to fetch profile:', error?.message);
      }
    };

    fetchData();
  }, []);

  const handleAvatarUpload = async (e) => {
    const file = e.target.files[0];
    if (!file || !session) return;

    setUploading(true);

    const fileExt = file.name.split('.').pop();
    const fileName = `${session.id}.${fileExt}`;
    const filePath = `avatars/${fileName}`;

    let { error: uploadError } = await supabase.storage
      .from('avatars')
      .upload(filePath, file, { upsert: true });

    if (!uploadError) {
      const { data } = supabase.storage.from('avatars').getPublicUrl(filePath);
      const publicUrl = data.publicUrl;

      await supabase
        .from('Players')
        .update({ avatar_url: publicUrl })
        .eq('id', session.id);

      setProfile((prev) => ({ ...prev, avatar_url: publicUrl }));
      setPreviewUrl(publicUrl);
    } else {
      alert('Failed to upload image.');
    }

    setUploading(false);
  };

  if (!profile) return <div className="text-white p-4">Loading profile...</div>;

  return (
    <div className="min-h-screen bg-gray-900 text-white px-4 py-10 flex flex-col items-center">
      <div className="bg-gray-800 p-6 rounded shadow-xl w-full max-w-md text-center">
        <h2 className="text-2xl font-bold mb-4">ðŸ‘¤ Your Profile</h2>

        <div className="flex justify-center mb-4">
          {previewUrl ? (
            <img src={previewUrl} alt="Avatar" className="w-24 h-24 rounded-full object-cover" />
          ) : (
            <div className="w-24 h-24 bg-gray-700 rounded-full flex items-center justify-center text-2xl font-bold">
              {profile.username?.[0]?.toUpperCase() || '?'}
            </div>
          )}
        </div>

        <input
          type="file"
          accept="image/*"
          onChange={handleAvatarUpload}
          className="text-sm text-gray-400 mb-4"
          disabled={uploading}
        />

        <div className="space-y-2 text-left">
          <p><strong>Username:</strong> {profile.username}</p>
          <p><strong>Email:</strong> {email}</p>
          <p><strong>Phone:</strong> {profile.phone || 'N/A'}</p>
          <p><strong>Level:</strong> {profile.level}</p>
          <p><strong>XP:</strong> {profile.xp}</p>
        </div>
      </div>
    </div>
  );
}

export default Profile;
