// src/components/Profile.js
import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

function Profile({ session }) {
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    const fetchPlayer = async () => {
      const { data } = await supabase
        .from('Players')
        .select('username, email, phone, level, xp, avatar_url')
        .eq('id', session.user.id)
        .single();
      setPlayer(data);
    };
    fetchPlayer();
  }, [session]);

  if (!player) return <div className="text-white p-6">Loading profile...</div>;

  return (
    <div className="max-w-md mx-auto text-white mt-10 bg-gray-900 p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Your Profile</h2>
      {player.avatar_url && (
        <img src={player.avatar_url} alt="Avatar" className="w-24 h-24 rounded-full mx-auto mb-4" />
      )}
      <p><strong>Username:</strong> {player.username}</p>
      <p><strong>Email:</strong> {player.email}</p>
      <p><strong>Phone:</strong> {player.phone || 'N/A'}</p>
      <p><strong>XP:</strong> {player.xp}</p>
      <p><strong>Level:</strong> {player.level}</p>
    </div>
  );
}

export default Profile;
