// src/components/Login.js
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate, Link } from 'react-router-dom';
import { motion } from 'framer-motion';

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (!error) navigate('/');
    else setErrorMsg(error.message);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-gray-900 via-gray-800 to-gray-950 text-white">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ type: 'spring', stiffness: 80 }}
        className="w-full max-w-md bg-gray-800/90 backdrop-blur p-8 rounded-xl shadow-2xl border border-purple-700"
      >
        <motion.h2
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
          className="text-4xl font-bold mb-6 text-center text-purple-400 drop-shadow"
        >
          üîê CyberQuest Login
        </motion.h2>

        {errorMsg && (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-red-400 mb-4 text-center"
          >
            {errorMsg}
          </motion.p>
        )}

        <form onSubmit={handleLogin} className="space-y-5">
          <motion.input
            whileFocus={{ scale: 1.02, borderColor: '#a855f7' }}
            type="email"
            placeholder="Email"
            className="w-full p-3 bg-gray-700 text-white rounded-lg border border-transparent transition-all duration-200 placeholder-gray-400"
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <motion.input
            whileFocus={{ scale: 1.02, borderColor: '#a855f7' }}
            type="password"
            placeholder="Password"
            className="w-full p-3 bg-gray-700 text-white rounded-lg border border-transparent transition-all duration-200 placeholder-gray-400"
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <motion.button
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.97 }}
            type="submit"
            className="w-full bg-purple-600 hover:bg-purple-700 transition-colors font-semibold py-2 rounded-lg shadow-md"
          >
            Login
          </motion.button>
        </form>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.7 }}
          className="mt-6 text-sm text-gray-400 text-center"
        >
          Don‚Äôt have an account?{' '}
          <Link to="/signup" className="text-purple-400 underline hover:text-purple-300">
            Sign up
          </Link>
        </motion.p>
      </motion.div>
    </div>
  );
}

export default Login;
